import "./Cart.css"

export default function AddToCart(props) {
    return (
        <div className="book-box">
            <div key={props.index} className="book-info">
                <div>Title: {props.book.title}</div>
                <div>Author: {props.book.author}</div>
                <div>Release date: {props.book.releaseDate}</div>
                <div>Summary: {props.book.shortBio}</div>
                {/* <div>Discount: {props.book.discount}</div> */}
                <div>Price: {props.book.price}</div>
            </div>
            <button className="cart-button" onClick={props.handleAddToCart}>Add {props.book.title} to cart</button>
        </div>
    )
}
