import './App.css';
import { useState } from 'react';
import BookArray from './model/BookArray'
import AddToCart from './components/AddToCart'

function App() {
  const [books, setBooks] = useState(BookArray)

  const [selectedIndexes, setSelectedIndexes] = useState([]);

  const handleAddToCart = (index) => {
    setSelectedIndexes([...selectedIndexes, index]);
  };

  const handleRemoveFromCart = (index) => {
    const updatedIndexes = selectedIndexes.filter((idx) => idx !== index);
    setSelectedIndexes(updatedIndexes);
  };

  const calculateTotal = () => {
    let totalPrice = 0;
    selectedIndexes.forEach((index) => {
      totalPrice += books[index].price;
    });
    return totalPrice;
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="books-container">
        <div className="header">Books</div>
          <div className="book-box-container">
            {books.map((book, index) => {
              const isSelected = selectedIndexes.includes(index);
              return (
                <AddToCart
                  key={index}
                  index={index}
                  book={book}
                  isSelected={isSelected}
                  handleAddToCart={handleAddToCart}
                />
              );
            })}
          </div>
        </div>
        <div className="cart">
          <div className="header">Cart</div>
          <div className="chosen-info">
            {selectedIndexes.map((index) => (
              <div className="cart-info" key={index}>
                <div>{books[index].title}</div>
                <div>{books[index].discount}</div>
                <div>{books[index].price}</div>
                <button className="cart-button-remove" onClick={() => handleRemoveFromCart(index)}>Remove</button>
              </div>
            ))}
          </div>
          <div>
            Total price: {totalPrice(price)}
          </div>
          </div>
      </header>
    </div>
  );
}

export default App;
