import './App.css';
import { useState } from 'react';
import Book from './model/Book.js'

function App() {
  const [books, setBooks] = useState([new Book(0, "React", ["First answer", "Second answer", "Third answer"], "JavaScript framework", "It works with JSX files.", false),
  new Book(1, "JSX", ["First answer", "Second answer", "Third answer"], "HTML + JavaScript", "Server-side scripting. Functions can be returned in pure HTML.", false),
  new Book(2, "XML", ["First answer", "Second answer", "Third answer"], "Extensible Markup Language", "XML was designed to carry data - with focus on what data is. Does not display data.", false)])

  const [targetIndex, setTargetIndex] = useState(0)

  return (
    <div className="App">
      <header className="App-header">
        <div className="glossary-container">
        {books.map((book, index) => (
            <div key={index} className="book">
              <button onClick={() => setTargetIndex(index)}>{book.question}</button>
              <div>{book.question}</div> {/* Display the question directly */}
            </div>
          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
