import "./Cart.css"

export default function Basket(props, book, index) {
    return (
        <>
        <div className="cart-info" key={index}>
            <div className="book-info"><span>Title:</span> {book.title}</div>
            <div className="book-info"><span>Price:</span> Â£{book.price} GBP</div>
            {book.discount && ( <div className="book-info"><span>Discount:</span> {book.discount}%</div> )}
            <button className="cart-button-remove" onClick={() => props.handleRemoveFromCart(index)}>Remove</button>
        </div>
        <div className="total">
            Total price: <b>{props.calculateTotal()}</b> GBP
        </div>
        </>
    )
}
