import "./QuestionButton.css"

export default function AddToCart(index, book, targetIndex, setTargetIndex) {
    const handleClick = () => {
        setTargetIndex(index);
    };
    return (
        <div key={index} className="book">
            <button onClick={handleClick}>{book.question}</button>
            {/* Render question only if it exists */}
            {book.question && <div>{book.question}</div>}
            {/* Render question based on targetIndex */}
            {book[targetIndex] && <div>{book[targetIndex].question}</div>}

            {/* <button onClick={setTargetIndex}>{book.question}</button>
            {book.question}
            <div>{book[targetIndex].question}</div> */}
        </div>
    )
}
