import './App.css';
import { useState } from 'react';
import BookArray from './model/BookArray'
import AddToCart from './components/AddToCart'

function App() {
  const [books, setBooks] = useState(BookArray)

  const [targetIndex, setTargetIndex] = useState(0)

  return (
    <div className="App">
      <header className="App-header">
        <div className="books-container">
          {books.map((book, index) => {
            return (
              <div className="book-box">
              <div key={index} className="book-info">
                <div>{book.title}</div>
                <div>{book.author}</div>
                <div>{book.releaseDate}</div>
                <div>{book.shortBio}</div>
                <div>{book.discount}</div>
                <div>{book.price}</div>
              </div>
                <button onClick={() => setTargetIndex(index)}>Add {book.title} to cart</button>
              </div>
            )
          })}
        </div>
        <div className="cart">{books[targetIndex].title}</div>
      </header>
    </div>
  );
}

export default App;
