import './App.css';
import { useState } from 'react';
import bookArray from './model/BookArray'
import AddToCart from './components/AddToCart'

function App() {
  const [book, setBook] = useState(bookArray)

  const toggleVisibility = (index) => {
    const updatedGlossary = [...book]
    updatedGlossary[index].isVisible = !updatedGlossary[index].isVisible
    setBook(updatedGlossary)
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="glossary-container">
          {book.map((item, index) => {
            return (
            <AddToCart key={index} item={item} toggleVisibility={() => toggleVisibility(index)}/>
            )
          })}
        </div>
      </header>
    </div>
  );
}

export default App;
