import './App.css';
import { useState } from 'react';
import BookArray from './model/BookArray'
import AddToCart from './components/AddToCart'

function App() {
  const [books, setBooks] = useState(BookArray)
  const [targetIndex, setTargetIndex] = useState(0)

  return (
    <div className="App">
      <header className="App-header">
        <div className="books-container">
          {books.map((book, index) => {
            return (
              <AddToCart setTargetIndex={() => setTargetIndex(index)} index={index} book={book}/>
            )
          })}
        </div>
        <div className="cart">
          <div className="header">Cart</div>
          <div>{books[targetIndex].title}</div>
          <div>{books[targetIndex].discount}</div>
          <div>{books[targetIndex].price}</div>
          </div>
      </header>
    </div>
  );
}

export default App;
