            <div className="book-info"><span>Price:</span> Â£{props.cartItem.price} GBP</div>
            {props.cartItem.discount && ( <div className="book-info"><span>Discount:</span> {props.cartItem.discount}%</div> )}
            <button className="cart-button-remove" onClick={() => props.handleRemoveFromCart(props.index)}>Remove</button>
        </div>
    )
}
